mapper.select:
SELECT BM.USERNAME, NAME, EMAIL, PHONE, FILENUM, FILENAME, ACCOUNTNUM, ACCOUNTDATE, BOOKNAME FROM BANKMEMBERS BM LEFT JOIN MEMBERFILE MF ON BM.USERNAME=MF.USERNAME
LEFT JOIN BANKACCOUNT BA ON BM.USERNAME=BA.USERNAME
LEFT JOIN BANKBOOK BB ON BA.BOOKNUM=BB.BOOKNUM WHERE BM.USERNAME=#{userName}
